// from https://thebookofshaders.com/06/
float rect(in vec2 st, in vec2 size){
	size = 0.25-size*0.25;
    vec2 uv = smoothstep(size,size+size*vec2(0.002),st*(1.0-st));
	return uv.x*uv.y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float y = uv.y;
    float x = uv.x;

    vec3 brn = vec3(0.745,0.696,0.529);
    vec3 blu = vec3(0.418,0.735,0.780);
    vec3 dar = vec3(0.065,0.066,0.290);
    vec3 gry = vec3(0.865,0.842,0.162);
    vec3 org = vec3(0.980,0.603,0.086);

    float inr_h = 0.2;

    vec3 mx_b = mix(dar, blu, step(0.5 + inr_h, y));
    vec3 mx_o = mix(org, gry, step(0.5 - inr_h, y));

    vec2 rct_s = vec2(0.01, 0.03);

    float bot = rect(
        vec2(uv.x, uv.y + inr_h + 0.085),
        rct_s
    );

    float top = rect(
        vec2(uv.x, uv.y - inr_h - 0.085),
        rct_s
    );

    vec3 col = vec3(0.);
    col += mix(mx_o, mx_b, step(0.5, y));
    col = mix(col, brn, bot);
    col = mix(col, brn, top);

    // Output to screen
    fragColor = vec4(col,1.0);
}
