void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = vec3(texture(iChannel0, uv));

    vec3 newCol = vec3(0.0);

    for (int i = 0; i < 3; i++) {
        float color = col[i];

    	float range1 = mod(iTime + uv.y - abs(uv.x - .5) + float(i) / 3.0, 3.0) - 1.0;
    	float range2 = mod(iTime * 1.5 - uv.x + abs(uv.y - .5) + float(i) / 2.0 + 0.5, 3.0) - 1.0;

        /*
    	if (color > range1 && color < range2) {
    		newCol[i] = 1.0;
    	}

    	if (color < range1 && color > range2) {
    		newCol[i] = 0.0;
    	}
		*/

        newCol[i] = (abs(color - range1) + abs(color - range2)) / 2.0;
    }

    fragColor = vec4(newCol, 1.0);
}
