float PI=3.1415926;
float Hash21(vec2 p){
    p = fract(p*vec2(234.34,435.345));
    p+=dot(p,p+34.23);
    return fract(p.x*p.y);
}
vec2 N22(vec2 p){
	vec3 a = fract(p.xyx*vec3(123.34,234.34,345.65));
    a+=dot(a,a+34.45);
    return fract(vec2(a.x*a.y,a.y*a.z));
}
float aa = 0.;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv.x*=iResolution.x/iResolution.y;
    uv.x+=.011;

    //uv= (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec2 screen = (fragCoord-.5*iResolution.xy)/iResolution.y;
    float backgroundColor =smoothstep(-.01,.01, cos(screen.x*PI)+.035);

    vec3 col = vec3(0);
    uv *= 5.;

   	vec2 c = fract(uv);

    vec2 id = floor(uv)+.5;
    float n=Hash21(id);

    float width=.1;

    float t = iTime;
    aa = abs(sin(t));    
    vec2 edge =sin(c*PI);
    edge.x-=clamp(aa*2.-n,width,1.);
    edge.y-=width;

    col+=step(.01,edge.x);
    col*=step(.01,edge.y);
    col=mix(vec3(0.),col,backgroundColor);


    fragColor = vec4(col,1.0);
}
