renderpass.code:
vec2 r( vec2 p ) {
    return fract(sin(vec2(dot(p,vec2(127.1,311.7)),dot(p,vec2(269.5,183.3))))*43758.5453);
}

float v(vec2 n)
{
	float d = 2.0;
	vec2 nfl = floor(n);
	vec2 nfr = fract(n);

	vec2 p;
	vec2 ns = sign(nfr - vec2(0.5));
	for(float x = 0.0; x < 2.0; x++)
	{
	 	for(float y = 0.0; y < 2.0; y++)
	 	{
	 	    vec2 ne = vec2(x,y) * ns;

	 	    p = r(nfl + ne) * 0.7 + 0.15 + ne;
            //p = r(nfl + ne) + ne; //This will create artefacts
	 	    d = min(distance(p, nfr), d);
	 	}
	 }

	 return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.y;
    uv *= 10.0;
    uv += iTime * 0.3;

    // Output to screen
    fragColor = vec4(vec3(v(uv)),1.0);
}
