void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    float a = 5.*iTime+uv.x;
    float b = 1.*iTime+uv.x+2.*uv.y;
    float c = 10.*iTime+uv.x-2.*uv.y;

    vec3 black = vec3(0,0,0);
    vec3 red = vec3(1,0,0);
    vec3 skyblue = vec3(0,0.5,1);
    vec3 grey = vec3(0.2,0.3,0.5);

    vec3 col = red*cos(20.*a)
                    +
               skyblue*cos(b)+cos(2.*b)+0.5*cos(5.*b)
                    +
               grey*sin(c);

    // Output to screen
    fragColor = vec4(col,1.0);
}
