float Circle(vec2 uv, vec2 p, float r, float blur){

  float d = length(uv-p); 
  float c = smoothstep(r,r-blur,d);
  return c;

}

float Band(float t, float start, float end, float blur){

    float step1 = smoothstep(start-blur, start+blur,t);
    float step2 = smoothstep(end+blur, end-blur,t);
    return step1*step2;

}

float Rect(vec2 uv, vec2 center, float size, float left, float right, float bottom, float top, float blur){
    uv-= center;
    uv/= size;

float band1 = Band(uv.x, left, right, blur);
float band2 = Band(uv.y, bottom, top, blur);
return band1*band2;
}

mat2 rotationMat(float a){
    float c= cos(a);
    float s=sin(a);
    return mat2(c,s,-s,c);
}

float Smiley(vec2 uv, vec2 p, float rot, float size){

