void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec2 gv = fract(uv * 3.0)-0.5;
    uv = cos(uv*iTime*0.2);
    vec3 col = vec3(0);

    float d = fract(distance(uv, vec2(-0.5,0))*10.*sin(iTime*0.5)); 
    d += fract(distance(uv, vec2(0.5,0))*3.14159*sin(iTime*0.8589));

    float b = fract(distance(uv, vec2(0.,0.1))*8.14159*sin(iTime*1.589));
    b += fract(distance(uv, vec2(0.,-0.1))*5.14159*sin(iTime*0.889));

    d = smoothstep(0.5,0.1,d);
    b = smoothstep(0.5,0.1,b);    
    col = vec3(d,b,0.3);

    fragColor = vec4(col,1.0);
}
