void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 u = vec2(fragCoord -.5 * iResolution.xy) / iResolution.y;
    vec2 mult = texture(iChannel0,vec2(0,u.y) * .25).rg;
    u.x += (mult.r - .5) * iTime * .5;

    float col = (pow(texture(iChannel1, u).r,2.));

    fragColor = vec4(vec3(col),1.0);
}
