// === draw iDate.w digit + visual count

void mainImage( out vec4 O, vec2 U )
{
    int   T = DIGIT( T(U).w );     // iDate.w at pixel shader launch
    float t = float(T);            // test time digit

    U /= iResolution.xy;
    float x = U.x*10.;

    if ( floor(x) == t )  O.rg ++; // target
    if ( floor(x) <= t && ceil(x) < iTime ) O.b ++; // progression

    if (fract(x) < .1) O.r++;      // separators
}

// === beep iDate.w digit 

vec2 mainSound( in int samp, float time )
{
    int   T = DIGIT(iDate.w);    // iDate.w at sound shader eval
    float t = float(T);          // test time digit
    return floor(time) < t+1.  && fract(time) > .7
        ? vec2( sin(6.2831*440.*time) )
        : vec2(0);
}

// === sample iDate at pixel shader launch

void mainImage( out vec4 O, vec2 U )
{
    O = iFrame==0 ? iDate : T(U);
}

