//initial code taken from http://pixelshaders.com/examples/quasicrystal.html
// click and drag to zoom in / out

float time;
float delta = 0.01;
vec2 p;

float wave(vec2 p, float angle) {
  vec2 direction = vec2(cos(angle), sin(angle));
  return cos(dot(p, direction));
}

float wrap(float x) {
  return abs(mod(x, 2.)-1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 m = iMouse / iResolution.xxxx;
    if (m.x < delta && m.y < delta){
    	p = (fragCoord / (iResolution.x * 0.9 / 10.) * (iResolution.y * 0.2 / 10.)) * .5;

    }
    else{
        p = (fragCoord / (m.x+delta / 10.) * (m.y+delta / 10.)) * .5;
}

  float brightness = 0.;
  for (float i = 1.; i <= 11.; i++) {
    brightness += wave(p, (iTime / 5.0 ) / i);
  }

  brightness = wrap(brightness);

  fragColor.xyz = vec3(brightness);
  fragColor.a = 1.;
}
