#define SMOOTH_RANGE 0.03
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float scale = 20.;
    vec4 tex0 = texture(iChannel0, vec2(uv.x/scale, 0));
    vec4 tex1 = texelFetch(iChannel0, ivec2(int(uv.x * 512./scale), 0), 0);
    vec3 col = vec3(mix(tex0.x, tex1.x, 1.));

    // smooth
    float avg = 0.;
    float steps = 10.;
    float step_size = SMOOTH_RANGE/steps;
    for (float x = 0.0; x < SMOOTH_RANGE; x += step_size) {
        float left = (uv.x - x > 0.0) ? uv.x - x : 0.0 - (uv.x - x);
        float right = (uv.x + x < 1.0) ? uv.x + x : 2.0 - (uv.x + x);
        avg += texture(iChannel0, vec2(left/scale , 0)).x;
        avg += texture(iChannel0, vec2(right/scale, 0)).x;
    }
    // Get medium value
    avg /= steps;
    avg /= 2.0;

    //float m0 = smoothstep(tex0.x, tex0.x-.01, uv.y);
    float m0 = smoothstep(avg, avg-.01, uv.y);
    float m1 = smoothstep(tex1.x, tex1.x-.01, uv.y);



    col = vec3(mix(m0, m1, .5 + .5*sign(sin(iTime*2.))));
    fragColor = vec4(col,1.0);
}
