float x( in vec2 p, in vec2 b )
{
    vec2 d = abs(2.0*p)-b;
    return (length(max(d,0.0)) + min(max(d.x,d.y),0.0))-0.02;
}

void mainImage(out vec4 o,in vec2 c)
{
	vec2 u=(c*2.0-iResolution.xy)/iResolution.y; 

	u.y+=-0.05*sin(u.x*30.0)-0.02*iTime-0.2*u.x*u.x;
	u.x+=-0.1*iTime;
    u.x = mod(u.x, 1.0);
    u.y = mod(u.y, 1.0);

	o=vec4(step(1.0,1.0-x(vec2(u.x-0.33,u.y),vec2(0.0))),
		step(1.0,1.0-x(u,vec2(0.62,0.03))),0,1.0);
}
