// DAWN BRINGER 16 COLOR PALETTE
const vec3 col0 = vec3(020./255., 012./255., 028./255.);
const vec3 col1 = vec3(068./255., 036./255., 052./255.);
const vec3 col2 = vec3(048./255., 052./255., 109./255.);
const vec3 col3 = vec3(078./255., 074./255., 078./255.);
const vec3 col4 = vec3(133./255., 076./255., 048./255.);
const vec3 col5 = vec3(052./255., 101./255., 036./255.);
const vec3 col6 = vec3(208./255., 070./255., 072./255.);
const vec3 col7 = vec3(117./255., 113./255., 097./255.);
const vec3 col8 = vec3(089./255., 125./255., 206./255.);
const vec3 col9 = vec3(210./255., 125./255., 044./255.);
const vec3 colA = vec3(133./255., 149./255., 161./255.);
const vec3 colB = vec3(109./255., 170./255., 044./255.);
const vec3 colC = vec3(210./255., 170./255., 153./255.);
const vec3 colD = vec3(109./255., 194./255., 202./255.);
const vec3 colE = vec3(218./255., 212./255., 094./255.);
const vec3 colF = vec3(222./255., 238./255., 214./255.);

void mainImage(out vec4 fragColor, in vec2 fragCoord)
	{
    vec2 uv = fragCoord/iResolution.xy;
    vec4 tex = texture(iChannel0,uv);
    vec3 col = tex.rgb;

    float gamma = 1.5;
    col.r = pow(abs(col.r),gamma);
    col.g = pow(abs(col.g),gamma);
    col.b = pow(abs(col.b),gamma);

    float dist0 = length(col - col0);
    float dist1 = length(col - col1);
    float dist2 = length(col - col2);
    float dist3 = length(col - col3);
    float dist4 = length(col - col4);
    float dist5 = length(col - col5);
    float dist6 = length(col - col6);
    float dist7 = length(col - col7);
    float dist8 = length(col - col8);
    float dist9 = length(col - col9);
    float distA = length(col - colA);
    float distB = length(col - colB);
    float distC = length(col - colC);
    float distD = length(col - colD);
    float distE = length(col - colE);
    float distF = length(col - colF);

    float d = min(min(dist0,dist1),min(dist2,dist3));
          d = min(min(dist4,dist5),d);
          d = min(min(dist6,dist7),d);
          d = min(min(dist8,dist9),d);
          d = min(min(distA,distB),d);
          d = min(min(distC,distD),d);
          d = min(min(distE,distF),d);

    if(d == dist0)
        col = col0;
    else if(d == dist1)
        col = col1;
    else if(d == dist2)
        col = col2;
    else if(d == dist3)
        col = col3;
    else if(d == dist4)
        col = col4;
    else if(d == dist5)
        col = col5;
    else if(d == dist6)
        col = col6;
    else if(d == dist7)
        col = col7;
    else if(d == dist8)
        col = col8;
    else if(d == dist9)
        col = col9;
    else if(d == distA)
        col = colA;
    else if(d == distB)
        col = colB;
    else if(d == distC)
        col = colC;
    else if(d == distD)
        col = colD;
    else if(d == distE)
        col = colE;
    else
        col = colF;

    if (fragCoord.x > iMouse.x)
    	fragColor = vec4(col.rgb,tex.a);
    else
        fragColor = tex;
	}
