#define pi 3.1415926

float smoothed_triangle(float d, float x)
{  return 1.0 - 2.0 * acos(d * sin(x)) / pi; }

float smoothed_square(float d, float x)
{  return d * 2.0 * atan(sin(x) / (1.0 - d)) / pi; }

float smoothed_sawtooth(float d, float x)
{
    return (1.0 - 2.0 * acos(d * -cos(x / 2.0)) / pi) * (2.0 *  atan(sin(x / 2.0) / (1.0 - d)) / pi);
}

float plucked_string(float x)
{
    // String beginns vibrating with many frequencies - over time only the lowest remains.
    // i.e. triangle -> sine OR square -> sine OR sawtooth -> sine (smoothed gradually)
    float d = pow(0.5, x / 1000.0);
    //return smoothed_triangle(d, x); // string?
    return smoothed_square(d, x); // metal?
    //return smoothed_sawtooth(d, x); // rubber?
}

