float midi2freq(int note)
{    return note > 0 ? 440.0 * pow(2.0, float(note - 69) / 12.0) : 0.; }

#define N(n, d) t -= float(d)*dt; if (t >= 0.) s += plucked_string(2.0*pi*midi2freq(n)* t );
