// variant of https://shadertoy.com/view/WtVXDW

#define C(q) t = max(t, 2.-length(mod(q.xy+4.,6.)-4.))

void mainImage(out vec4 O, vec2 U) {
    float t = iTime;
    mat2  R = mat2( sin(1.1+vec4(0,33,11,0)) );
    vec3  q = iResolution,
          D = vec3(.3*(U+U-q.xy)/q.y, -1),     // ray direction
          p = 23./q, a;                        // marching point along ray 
    O-=O;
    for ( O++; O.x > 0. && t > .01 ; O-=.015 )
        q = p,
        q.xz *= R, q.yz *= R,                  // rotation
        t-=t,
        a = abs(q),  
        t = max(t, max(max(a.x,a.y),a.z) -4. ),// cube 
        t = max(t, 3. - max(a.x,a.z)  ),       // negative cube 
        q.y -= iTime,
        C(q.xy),
        C(q.zy-vec2(0,3)),
        p += t*D;                              // step forward = dist to obj
}
