void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;

    float red = 0.;
    float green = 0.;
    float blue = 0.;

    //float colorChange1 = sin(iTime*2. + uv.x);
    //float colorChange2 = sin(iTime);
    //float colorChange3 = sin(iTime * .5);

    float timeMod = mod(iTime, 1.);
    float phaseShift = iTime * 3.14;

    if(.33*sin(5.*uv.x + phaseShift) + .5 >= uv.y - 0.1 && 
       .33*sin(5.*uv.x + phaseShift) + .5 <= uv.y + 0.1) 
    {
        float colorChange1 = sin(uv.y * sin(.33*iTime)) * sin(uv.x + uv.y*2.) + 0.15;
    	float colorChange2 = sin(uv.y * sin(.66*iTime)) * sin(.33*uv.x) + 0.15;
    	float colorChange3 = sin(uv.y * sin(iTime)) * sin(.66*uv.x + uv.y) + 0.15;

        red = colorChange1;
        blue += colorChange2;
        green += colorChange3;
    }


    // Output to screen
    fragColor = vec4(red, green, blue ,1.0);
}
