// Union
// a : field function of left sub-tree
// b : field function of right sub-tree
float Union(float a,float b)
{
    return min(a,b);
}



float Union(float a, float b, float c)
{
  return min(a, min(b, c ));   
}

float Union(float a, float b, float c, float d)
{
  return min(a, min(b, min(c ,d )));   
}

float Union(float a, float b, float c, float d, float e)
{
  return min(a, min(b, (min(c , min(d , e)))));   
}
float Union(float a, float b, float c, float d, float e, float f)
{
  return min(a, min(b, (min(c , min(d ,min( e,f))))));   
}

// Inter
// a : field function of left sub-tree
// b : field function of right sub-tree
float Inter(float a,float b)
{
    return max(a,b);
}


float Inter(float a, float b, float c)
{
  return max(a, max(b, c ));   
