// Sphere 
// p : point
// c : center of skeleton
// r : radius
float Sphere(vec3 p, vec3 c,float r)
{
  return length(p-c)-r;
}

// Plane 
// p : point
// n : Normal of plane
// o : Point on plane
float Plane(vec3 p, vec3 n, vec3 o)
{
    return dot((p-o),n);
}

//Cylindre infini
float CylInf( vec3 p, vec3 a, vec3 u, float r){
	float l = dot((p-a),u);
    float d = length(p-a);
    return sqrt( (d)*(d) - l*l ) - r;
}

//Cylindre
// p : point
float Cyl(vec3 p,vec3 a,vec3 b,float r){
    float d = length(b-a);
	float fc = CylInf(p,a,(b-a)/d,r);
    float fa = Plane(p,(b-a)/d,a);
    float fb = Plane(p,-(b-a)/d,b);

    return Diff(fc,Union(fa,fb));
}

//Capsule
float Capsule(vec3 p,vec3 a,vec3 b,float r){
	float fc = Cyl(p,a,b,r);
    float sa = Sphere(p,a,r);
    float sb = Sphere(p,b,r);
    return Union(fc,Union(sa,sb));
}

//Tore
float Tore(vec3 p,vec3 c,vec3 n,float R,float r){
    float ph = dot((p-n),n);
    float l = sqrt(length(c-p)*length(c-p) - ph*ph);
    float d = sqrt(length(l-R)*length(l-R) + ph*ph);
    return d-r;
}

//Cube
float Cube(vec3 p){
	float v =Inter ( Plane(p,vec3(0.0,0.0,-1.0),vec3(0.0,0.0,-1.0)),
    Plane(p,vec3(0.0,1.0,0.0),vec3(0.0,1.0,0.0)),
    Plane(p,vec3(0.0,0.0,1.0),vec3(0.0,0.0,1.0)),
    Plane(p,vec3(1.0,0.0,0.0),vec3(1.0,0.0,0.0)),
    Plane(p,vec3(-1.0,0.0,0.0),vec3(-1.0,0.0,0.0)),
    Plane(p,vec3(0.0,-1.0,0.0),vec3(0.0,-1.0,0.0)));
    return v;
}


