float pave(vec3 p,float o,float e,float h){
	float v = Plane(p,vec3(0.,0.,-1),vec3(0.,0.,-e +h));
    v = Inter(v,Plane(p,vec3(0.,1.,0.),vec3(0.,o,0. +h)));
