#define t iTime
#define r iResolution.xy

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	vec3 c;
	float l,z=t;
	for(int i=0;i<15;i++) {
		vec2 uv,p=fragCoord.xy/r;
		uv=p;
		p-=.50;
        p+=.50;
        p.x*=r.y/r.y;
		p.y*=r.x/r.x;
		z+=.780;
		l=length(p);
		uv+=p/l*(sin(z)+1.)*abs(sin(l*10.-z*25.5));
		c[i]=.01/length(abs(mod(uv,1.5)-.50));
	}

    for(int i=5;i<15;i++) {
		vec2 uv,p=fragCoord.xy/r;
		uv=p;
		p-=.80;
		p.y*=r.x/r.y;
		z+=.80;
		l=length(p);

	}

   for(int i=2;i<15;i++) {
		vec2 uv,p=fragCoord.xy/r;
		uv=p+r;
		p-=.80;
		z+=.80;
		l=length(p);

	}




	fragColor=vec4(c/l,t);
}
