vec3 DrawSphere(vec2 uv, vec2 cood, float r){
    vec3 col=(length(cood-uv)<=r)?vec3(1.):vec3(0.);
    return col;
}

vec3 DrawAdd(vec3 col, vec3 add){
    return max(col,add);
}
vec3 DrawSub(vec3 col, vec3 sub){
    return min(col,sub);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //octo color
    vec3 octocol = vec3 (224, 45, 45)/255.;
    vec3 test = vec3(0.);

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv-=.5;
    uv.x *= iResolution.x/iResolution.y;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    col = vec3(0.);


    // Output to screen
    //col = (abs(0.3-uv.x)<=0.05)?vec3(1.):col;



    //octo
    col = DrawAdd(col,DrawSphere(uv ,vec2(0.,.1),.3));
    col = DrawAdd(col,DrawSphere(uv ,vec2(0.,.068),.285));
    col = DrawAdd(col,DrawSphere(uv, vec2(-.13,0.),.3)-DrawSphere(uv, vec2(-.23,0.25),.4));
    col = DrawAdd(col,DrawSphere(uv-vec2(.33,0), vec2(-.2,0.),.3)-DrawSphere(uv-vec2(.33,0), vec2(-.1,0.25),.4));
    col -= DrawSub(col,DrawSphere(uv ,vec2(.1,.15),.04));
    col -= DrawSub(col,DrawSphere(uv ,vec2(-.1,.15),.04));

    //balloon
    col = DrawAdd(col,DrawSphere(uv ,vec2(.5,.3),.1));
    col = DrawAdd(col,DrawSphere(uv ,vec2(.5,.28),.095));
    col = DrawAdd(col,DrawSphere(uv ,vec2(.5,.25),.08));
    col = DrawAdd(col,DrawSphere(uv ,vec2(.5,.17),.015));


    col*=octocol;


    //face
    vec3 col1 = vec3(0.);
    col1 = DrawAdd(col1,DrawSphere(uv ,vec2(.09,.17),.01));
    col1 = DrawAdd(col1,DrawSphere(uv ,vec2(-.11,.17),.01));

    col1*=vec3(1.);
    col+=col1;

    //line    
    vec3 col2 = vec3(0.);
    col2 = (abs(0.45-uv.x+sin(uv.y*10.)*.05)<=0.01&&uv.y<=.157&&uv.y>=-.3)?vec3(1.):col2;

    col+=col2;

    fragColor = vec4(col,1.0);
}
