void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 o;
    o.y = iResolution.y / 2.0;

    for(float t =  -1.0; t <= 0.0; t += 0.01){
    	o.x = (sin(iTime + t) + 2.0) / 4.0 * iResolution.x;

    	if(distance(fragCoord, o) <= 50.0)
            fragColor = vec4(vec3(texture(iChannel0, fragCoord / iResolution.xy)) +
                             1.0 * t, 1.0);
    }
}
