float circle(vec2 st, float radius, float s){
    float pct = distance(st,vec2(0.5)) * 2.;
    pct = 1.-smoothstep(radius-s,radius+s, pct);
    return pct;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
	vec2 st = fragCoord.xy/iResolution.xy;
    float pct = 0.0;

    st.x *= iResolution.x/iResolution.y;
    st.x -= .38;

    float speed = 6.;
    float radius = (sin(iTime*speed)+1.)*.5; //normalized
    float doubleTime = (sin(iTime*speed*2.)+1.)*.5;
    radius += doubleTime*.5;
    radius += sin(iTime*speed*10.)*sin(iTime*speed)*.5; //vibrate
    //radius *= radius;

    //min/max
    radius += 2.;
    radius *= .2;

    float offset = sin(iTime*speed*.5)*.4;

    st.x += offset;
    float c1 = circle(st, radius*.8, (1.-radius)*.1);

    //red circle
    vec3 color = vec3(c1)*vec3(1.,0.1,0.2);

    //green circle
    st.x -= offset;
    float c2 = circle(st, radius, (1.-radius)*.2);
    color += vec3(c2)*vec3(0.,0.1,0.);

    //blue circle
    st.x -= offset;
    float c3 = circle(st, radius*.8, (1.-radius)*.2);
    color += vec3(c3)*vec3(0.,0.,1.);


	fragColor = vec4( color, 1.0 );
}

