vec2 c_prod(vec2 z1, vec2 z2) {
	return vec2((z1.x*z2.x)-(z1.y*z2.y), (z1.x*z2.y)+(z1.y*z2.x));
}

vec2 f(vec2 z, vec2 c) {
	return c_prod(z,z)+c;
}

vec3 div_color(int i) {
    return vec3(float(i%255)/255.);
}

vec2 map_pixel(vec2 pixel) {
	return (5./length(iResolution.xy))*(pixel-(iResolution.xy/2.))-vec2(0.,0.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 z_julia = map_pixel(fragCoord.xy);
    vec2 c_julia = map_pixel(iMouse.xy);

    vec2 z_mandelbrot = vec2(0,0);
    vec2 c_mandelbrot = z_julia;

    vec3 color_julia = vec3(0);
    vec3 color_mandelbrot = vec3(0);

    bool ready_mandelbrot = false;
    bool ready_julia = false;

    int i = 0;
    while(i<(255*3)) {
        if (!ready_mandelbrot) {
        	if (length(z_mandelbrot)>2.) {
        		color_mandelbrot = div_color(i);
        	}
    		z_mandelbrot = f(z_mandelbrot, c_mandelbrot);
        }

        if (!ready_mandelbrot) {
			if (length(z_julia)>2.) {
        		color_julia = div_color(i);
        	}
            z_julia = f(z_julia, c_julia);
        }
        if (ready_mandelbrot && ready_julia) break;
    	i++;
    }

    fragColor = vec4(color_mandelbrot+vec3(0,color_julia.x*2.,0),1.);
}
