void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord/iResolution.xy;

    // Reverse course of animation after 30 seconds
    float time = mod(iTime, 60.);
    if (time >= 30.) {
        time = 30. - mod(time, 30.);
    }
    time = iMouse.x / 100.;

    float red =
        0.7 +
        0.3 * cos((uv.x * 50. + cos(uv.y * 40.) / 5. + cos(time * 5.)) * 1.);

    float green =
        0.5 +
        0.3 * cos(uv.x * 50. + cos(uv.y * 40.) / 5. + time * 5.) +
        0.2 * sin(uv.y * cos(time*.2) * 5. * uv.x * cos(time / 3. * uv.x) * 10.);

    float blue =
        cos(time) / 2. +
        sin(time / 3.);

    // debug visualization of time
    bool showTimer = true;
    float timerScale = 100.;
    float blinkTick = mod(floor(time * 30.), 3.);
    float x = uv.x * iResolution.x; // x-position in pixels
    float y = uv.y * iResolution.y; // y-position in pixels

    float magic = iMouse.x / iResolution.x;

    bool timerCondition = showTimer && (
        (
            blinkTick == 0. && mod(floor(y), 6.) <= 3. ||
            blinkTick == 1. && mod(floor(y), 6.) >= 2. && mod(floor(y), 6.) <= 5. ||
            blinkTick == 2. && (mod(floor(y), 6.) <= 1. || mod(floor(y), 6.) >= 4.)
        ) &&
        x > floor(mod(time, iResolution.x / timerScale) * timerScale + y / 4.) &&
        x < ceil(mod(time, iResolution.x / timerScale) * timerScale + y / (4. - (cos(time * 16.) + 1.) * .1))
    );

    if (timerCondition)
    	fragColor = vec4(0.);
	else
    	fragColor =
        	vec4(red, green, blue, 1.)
        	+ (cos(time) + 1.) / 2. * vec4(green, blue, red, 1.);
}
