// https://en.wikipedia.org/wiki/Difference_of_Gaussians
// https://en.wikipedia.org/wiki/Blob_detection

float Ires()
{
    return iChannelResolution[0].x;
}

vec4 I(vec3 U)
{
    vec4 C = texture(iChannel0, U.xy, U.z);
    //return C;
    return vec4(dot(C, vec4(.5, .7, .1, 0.)));
}

vec4 L(vec3 U)
{
    vec4 C = vec4(0);
    float s = U.z;
    float w = 0.;
    for (float dy=-3. ; dy<=3. ; dy+=.2)
    {
    	for (float dx=-3. ; dx<=3. ; dx+=.2)
        {
            vec2 p = vec2(dx, dy);
            float f = exp(-dot(p, p)/2.);
            p *= s;
            C += f * I(vec3(U.xy + p, log2(s*Ires())-1.5));
            w += f;
        }
    }
    C /= w;
    return C;
}

vec4 G(vec4 U)
{
    return L(U.xyz) - L(U.xyw);
}

vec4 Gnorm(vec4 U)
{
    return U.z / (U.z - U.w) * G(U);
}

void mainImage( out vec4 C, in vec2 U )
{
    U /= iResolution.xy;
    float cycle = (1. + sin(iTime / 3.)) * .5;

    C = Gnorm(vec4(U, vec2(5, 1) * pow(10., mix(-3., -1., cycle)))) * vec4(1,-1,0,0);
}
