const float PI = 3.1415926535897932384626433832795;
const float res_x = 64.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

//    float t = clamp(10.0*sin(iTime),-9.5,9.5);
	float t = iTime;


    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    vec3 col = vec3(0.0,0.0,0.0);
    float xb = floor( res_x * uv.x )/res_x;
    if ( abs(uv.x - xb) < 0.1/res_x ) col.x = 0.0;
    else if ( uv.y+0.05*sin(4.0*PI*xb+t) < 0.15 ) col.x = 0.4;
    else if ( uv.y+0.05*sin(4.0*PI*xb+t) > 0.85 ) col.x = 0.4;
    // Output to screen
    fragColor = vec4(col,1.0);
}
