renderpass.code:
vec2 rot(vec2 p, float deg)
{
	mat2 rt = mat2(cos(deg),sin(deg), -sin(deg), cos(deg));
    return p*rt;
}
float noise(vec2 p)
{
	return fract(sin(p.x * 234.345 * iTime+p.y*456.234)*235.23);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec3 col;
    float ang;

    for(float i = .0; i<2.; ++i)
    {
        float len = length(rot(uv,iTime)+noise(uv*i)-.5);       //去掉noise有惊喜
		ang = asin(.001/len) * 100.;                             //注意精度问题
        col += ang * vec3(.5,.5, noise(uv*i)) * abs(sin(iTime));
    }

    //float lig =  asin(.001/length(uv)) * 100.;
    //col = vec3(.5,.5,1.) * lig;
    fragColor = vec4(col,1.0);
}
