vec2 hash( vec2 p ) // replace this by something better
{
	p = vec2( dot(p,vec2(127.1,311.7)), dot(p,vec2(269.5,183.3)) );
	return -1.0 + 2.0*fract(sin(p)*43758.5453123);
}

float snoise( in vec2 p )
{
    const float K1 = 0.366025404; // (sqrt(3)-1)/2;
    const float K2 = 0.211324865; // (3-sqrt(3))/6;

	vec2  i = floor( p + (p.x+p.y)*K1 );
    vec2  a = p - i + (i.x+i.y)*K2;
    float m = step(a.y,a.x); 
    vec2  o = vec2(m,1.0-m);
    vec2  b = a - o + K2;
	vec2  c = a - 1.0 + 2.0*K2;
    vec3  h = max( 0.5-vec3(dot(a,a), dot(b,b), dot(c,c) ), 0.0 );
	vec3  n = h*h*h*h*vec3( dot(a,hash(i+0.0)), dot(b,hash(i+o)), dot(c,hash(i+1.0)));
    return dot( n, vec3(70.0) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;    

    // create an RGB color displacement glitch
    float d= 0.0;
    if (uv.y > rand(vec2(iTime,iTime))) {
	    // displacement amount
        d= snoise(vec2(iTime*3.0, uv.y * 0.2));
        d = max(0.0, d - 0.1);	// crop wave for burst
        d =  d * d * 0.5;		// amplify 
    } else {
        // create discontinuity/edge
    }

    // displace RGB color components  
	fragColor =   texture(iChannel0, vec2(uv.x        , uv.y));
	fragColor.g = texture(iChannel0, vec2(uv.x - d*0.5, uv.y)).g;
	fragColor.b = texture(iChannel0, vec2(uv.x - d    , uv.y)).b;


    // add fake raster lines to glitch
    if (floor(mod(fragCoord.y * 0.333, 2.0)) == 0.0) {
        fragColor.rgb *= 1.0 - (0.8 * d);
    }

}
