#define TWO_PI 6.2831853

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;

    vec2 pv = vec2(atan(uv.x,uv.y),length(uv));

    uv = vec2(pv.x/TWO_PI+0.5,pv.y);

    float a = TWO_PI*TWO_PI;
    float b = TWO_PI/2.;

	uv.x += iTime*.01;
    uv.y += -iTime*.1;
    float d = b*tan(uv.x/TWO_PI*a)*cos(uv.x/TWO_PI*a)/tan(sin(uv.y/TWO_PI*a));


    d = floor(d*2.)/16.;
    vec3 col = vec3(d/2.,d-.4,smoothstep(1.,d,pv.y));


    fragColor = vec4(col,1.0);
    fragColor = pow(fragColor,vec4(1./2.2))-.1;
}
