float rect(vec2 p, float size){
   float left = step(0.5-size/2.0,p.x);
   float right = step(0.5-size/2.0,1.0-p.x);
   float height = size*iResolution.x/iResolution.y;
   float top = step(0.5-height/2.0,p.y);
   float bottom = step(0.5-height/2.0,1.0-p.y);
   return left*right*top*bottom;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.xy;
    vec3 color = vec3(rect(uv,0.3));
	fragColor = vec4(color,1.0);
}
