#define TAU 6.28319
// hsb to rgb.
vec3 getRGB(float h, float s, float b){
    vec3 c = vec3(h, s, b);
    vec3 rgb = clamp(abs(mod(c.x * 6.0 + vec3(0.0, 4.0, 2.0), 6.0) - 3.0) - 1.0, 0.0, 1.0);
    rgb = rgb * rgb * (3.0 - 2.0 * rgb);
    return c.z * mix(vec3(1.0), rgb, c.y);
}
float lineValue(vec2 p, float n, float d, float phase){
  float value = 0.0;
  float t;
  for(float i = 0.0; i < n; i += 1.0){
    t = TAU * i / n + phase;
    if(dot(p, vec2(sin(t), cos(t))) < d){ value += 1.0; }
  }
  return value;
}
// mainImage.
void mainImage( out vec4 fragColor, in vec2 fragCoord ){
  vec2 p = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);
  vec3 col = vec3(0.0);
  float v1 = lineValue(p, 5.0, 0.3, iTime * (-0.1));
  float v2 = lineValue(p, 13.0, 0.1, iTime * 0.2);
  float v3 = lineValue(p, 17.0, 0.4, iTime * 0.1);
  float v4 = lineValue(p, 31.0, 0.6, iTime * (-0.2));
  float v = fract((v1 + v2 + v3 + v4) / 11.0);
  col = getRGB(fract(iTime * 0.05), 1.0, v);
  fragColor = vec4(col, 1.0);
}
