// variant of https://shadertoy.com/view/WtXyRN

#define B(a) max(a.x,max(a.y,a.z))                // box

void mainImage(out vec4 O, vec2 U) {

    float T = iTime, t=1.,c=t,m=1e9;
    mat2  R = mat2( cos(.3*T +vec4(0,33,11,0)) ); // rotation
    vec3  q = iResolution, a,
          D = vec3(.3*(U+U-q.xy)/q.y, -1),        // ray direction
          p = 30./q;                              // marching point along ray 
    O-=O;
    for ( O++; O.x > 0. && t > .01 ; O-=.015 )
        q = p.zxy,
        q.xy *= R,
        a = abs(q), t = abs(B(a)-6.)-.2,          // hollow cube
        t = max(t,  -(min(a.x,min(a.y,a.z))-3.) ),// carve
        t = min(t, c = length(q) - 5.),           // central sphere
        m = min(m,c),                             // closed distance to sphere
        p += .7*t*D;                              // step forward = dist to obj
    c==t ? O *= vec4(.5,0,0,1)                    // color inner sphere
         : O += (.5+.5*sin(T))* exp(-.2*m)*vec4(.5,0,0,1); // glow
}
