void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));


    vec4 disp = texture(iChannel0, uv + vec2(iTime * 0.01, iTime * 0.02));

    float resx = iResolution.x;
    float resy = iResolution.y;

    vec2 center = (vec2(sin(iTime) * resx * 0.75 * 0.5, cos(iTime) * resy * 0.6 * 0.5) + 
                   vec2(sin(2.0*iTime) * resx * 0.25 * 0.5, cos(2.0*iTime) * resy * 0.4 * 0.5) +
                   vec2(resx * 0.5, resy * 0.5));
    float dist = length(fragCoord - center);
    float mag = exp(-dist * (0.01 + 0.005 * sin(iTime)));


    disp = disp * mag;

    vec4 col2 = texture(iChannel1, uv + disp.rg * 0.2);

    // Output to screen
    fragColor = col2;
}
