#define PI 3.14159

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 p = 10.0 * ((2.0 * fragCoord - iResolution.xy) / iResolution.y);

    float a = atan(p.y, p.x) / PI + 1.;
    float r = length(p);

    vec3 col = 0.5 + 0.5*cos(iTime + vec3(0,2,4))
        + step(mod((r - 0.5)/2. + a, 2.), 0.5) * vec3(0.2, 0.02, -0.02)
        + step(mod(floor(r/2. + a + 1.) * a + mod(iTime, 2.0), 0.5), 0.2) * vec3(0.4, 0.02, 0.03);

    // Output to screen
    fragColor = vec4(col,1.0);
}
