void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = texelFetch(iChannel0,ivec2(fragCoord),0);
}

const float E = 0.001;
const float FAR = 40.0;
const int STEPS = 64;
const float START = 349.0;

vec3 glow = vec3(0.0);

float time = 0.0;



void rot(inout vec2 p, float a) {
    p = cos(a)*p + sin(a)*vec2(p.y, -p.x);
}

float sphere (vec3 p, float s)
{
    return length(p)-s;
}

float box (vec3  p, vec3 r){
    vec3 d = abs(p) - r;
    return length(max(d,0.0) + min(max(d.x, max(d.y, d.z)), 0.0));
}

float twistBox( vec3 p, vec3 r )
{
    const float k = 2.0; // or some other amount
    float c = cos(k*p.y);
    float s = sin(k*p.y);
    mat2  m = mat2(c,-s,s,c);
    vec3  q = vec3(m*p.xz,p.y);
    return box(q,r);
}

float scene(vec3 p){
    vec3 pp = p;
    for (int i = 0; i < 4; ++i) {
        pp = abs(pp) - vec3(1.0,2.0,4.0);
        rot(pp.xz,time*.01);
        rot(pp.xy,time*.1);
    }
    vec3 br = pp;
    rot(br.zx,time);
    float a = sphere(pp, 1.0);
    float b = twistBox(br, vec3(.1,0.5,1.0));
    float c = box(pp, vec3(.3,1.0,1.0));
    float h = clamp( 0.5 + 0.5*(a-c)/a, 0.0, 1.0 );
    float z = max(a,-c);
    vec3 g = vec3(0.1, 0.2, 0.6)*0.1 / (0.01+abs(max(a,b)));
    glow += g*0.5;
    return smoothstep(FAR*length(g),max(b, -z)*2.,2.1);//max(b, -z)*2.;
}

float march(in vec3 ro, in vec3 rd, out vec3 p)
{
    p = ro;
    float t = E;
    for (int i = 0; i < STEPS; ++i) {
        float d = scene(p);
        t += d;        
        if (d < E || t > FAR) {
            break;
        }
        p += rd*d;
    }
    return t;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    time = START+iTime;
    vec3 cp = vec3(0, 0, 10);
    vec3 ct = vec3(0,0,-1);

    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 q = -1.0+2.0*uv;
    q.x *= iResolution.x/iResolution.y;

    vec3 cf = normalize(ct-cp);
    vec3 cr = normalize(cross(vec3(0.0,1.0,0.0),cf));
    vec3 cu = normalize(cross(cf,cr));

    vec3 rd = normalize(mat3(cr,cu,cf)*vec3(q,radians(60.0)));

    vec3 p = vec3(0.0);

    float t = march(cp,rd,p);

    vec3 col = vec3(0.0);
    if(t < FAR){
        col = vec3(0.2,0.6,0.8) * length(p) *0.025;
    }

    col = smoothstep(0.0,1.0,col);
    col *= (1.0-vec3(t/FAR));
    col += glow;



    // Output to screen
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 bufB = texelFetch(iChannel1,ivec2(fragCoord),0);
    vec4 bufA = texelFetch(iChannel0,ivec2(fragCoord),0);
    //fragColor = bufA;
    fragColor = mix(bufB,bufA,0.125);
}
