void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    vec2 uv = fragCoord/iResolution.xy;
	vec4 tex = texture(iChannel1, uv); // CHANGE TO iChannel0 to use static img instead of webcam
    float speed = 0.7;

    float threshold = (sin(iTime * speed) + 1.0) / 2.0;
    float L = 0.2126 * tex.r + 0.7152 * tex.g + 0.0722 * tex.b;

    vec4 final;

    if (L > threshold) {
        final = tex;
    } else {
        final = vec4(0.0, 0.0, 0.0, 0.0);
    }

    // Output to screen
    fragColor = final;
}
