#define STRENGTH 0.05
#define RAD 0.3
#define SPEED 0.5

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 mouse = iMouse.xy / iResolution.xy;

    vec2 noise = texture(iChannel0, uv+iTime*SPEED).xy;
    float filt = smoothstep(RAD, 0., length (mouse - uv));


    uv.x += cos (noise.x) *STRENGTH * filt;
    uv.y += sin (noise.y) *STRENGTH * filt;


    vec4 appSurf = texture(iChannel1, uv);

    // Output to screen
    fragColor = appSurf;
}
