void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	float ratio = iResolution.x/iResolution.y;
	uv.x *= ratio;    

    vec2 center1 = vec2(0.5*ratio * cos(iTime)* 3., 0.5 * cos(iTime) * 3.);
    vec2 center2 = vec2(0.5*ratio  /*sin(iTime)*/, 0.5);
    vec2 center3 = vec2(0.4*ratio,0.4);

    float dist = distance(uv, center1);

   	dist = distance(uv, center1) * distance (uv,center2);
   	//dist = distance(uv, center2) / distance (uv,center1) * distance(uv,center3);
  	//dist = distance(uv, center2) - distance (uv,center1) / distance(uv,center3);

    vec3 color = vec3(20.);
    vec3 red = vec3(11,0,0);
    float t = sin(iTime * 1.) * .1;
    //color = vec3(1.-fract(smoothstep(0.2+ t,.25+t,dist)));
 	color = vec3(step(0.2+ t,dist));

    // Output to screen
    fragColor = vec4(mix(vec3(0,23,1),color,red),.0);

}
