float opU( float d1, float d2 ) { return  min(d1,d2); }
float opS( float d1, float d2 ) { return max(-d1,d2); }
float opI( float d1, float d2 ) { return max(d1,d2); }


vec2 opU2( vec2 d1, vec2 d2 ) 
{
    if (d1.x<d2.x) return d1;
    else return d2;
}

vec2 opS2( vec2 d1, vec2 d2 )
{
    if  (-d1.x<d2.x) return d1;
    else  return d2;
}

vec2 opI2( vec2 d1, vec2 d2 ) 
{
    if (d1.x>d2.x)	return d1;
    else return d2;
}

vec2 opS(vec2 d1, vec2 d2) {
	return vec2(max(-d2.x, d1.x), 2.0);
}

vec2 opRep2D( in vec2 p, in vec2 c )
{    vec2 q = mod(p+0.5*c,c)-0.5*c; return  q; }

float sdCirc(vec2 p, float r)
{  return length(p)-r; }


float sdBox( in vec2 p, in vec2 b )
{ vec2 d = abs(p)-b;  return length(max(d,0.0)) + min(max(d.x,d.y),0.0);}


vec2 Pistaold(vec2 p)
{

    vec2 c1=vec2(10.0,4.0);
    vec2 pr1=opRep2D( p,c1);

    float sbIzq= sdBox(pr1-vec2(1.0,0.0), vec2(0.02,2.0) );
    float sbDer= sdBox(pr1-vec2(-1.0,0.0), vec2(0.02,2.0) );

    vec2 c2=vec2(10.0,2.0);
    vec2 pr2=opRep2D( p,c2);
    float sbCentro= sdBox(pr2-vec2(0.0,0.0), vec2(0.02,0.2) );

    vec2 resp=vec2(9999.9,-1.0);
    resp=opU2(resp, vec2( sbIzq, 0.0));
    resp=opU2(resp, vec2( sbDer, 0.0));
    resp=opU2(resp, vec2( sbCentro,1.0));


    return resp;

}

vec2 Camino(vec2 p)
{
   p.x=p.x+sin(p.y*0.5);
   return p; 
}


vec2 Pista(vec2 p)
{
   // p=Camino(p);
    vec2 c1=vec2(100.0,4.0);
    vec2 cpasto=vec2(10.0,1.0);
    vec2 pr1=opRep2D( p,c1);
    vec2 ppasto=opRep2D( p,cpasto);
    float sbPavimento= sdBox(pr1-vec2(0.0,0.0), vec2(1.0,2.5) );
    float sbIzq= sdBox(pr1-vec2(1.0,0.0), vec2(0.02,2.0) );
    float sbDer= sdBox(pr1-vec2(-1.0,0.0), vec2(0.02,2.0) );
    vec2 c2=vec2(10.0,2.0);
    vec2 pr2=opRep2D( p,c2);
    float sbCentro= sdBox(pr2-vec2(0.0,0.0), vec2(0.02,0.2) );
    vec2 resp=vec2(9999.9,-1.0);
    sbPavimento=opS(sbCentro,  sbPavimento);
    resp=opU2(resp, vec2( sbPavimento, 9.0));
    resp=opU2(resp, vec2( sbIzq, 2.0));
    resp=opU2(resp, vec2( sbDer, 2.0));
    resp=opU2(resp, vec2( sbCentro,1.0));


    return resp;

}




vec2 Vehiculo(vec2 p )
{
    float sBase= sdBox(p-vec2(0.0,-0.5), vec2(0.1,0.2) );

    float sLlantaIzqA= sdBox(p-vec2(-0.12,-0.4), vec2(0.03,0.06) );
    float sLlantaDerA= sdBox(p- vec2(0.12,-0.4), vec2(0.03,0.06) );
    float sLlantaIzqB= sdBox(p-vec2(-0.12,-0.6), vec2(0.03,0.06) );
    float sLlantaDerB= sdBox(p-vec2( 0.12,-0.6), vec2(0.03,0.06) );

    vec2 resp=vec2(9999.9,-1.0);

    resp=opU2(resp, vec2( sBase, 0.0));
    resp=opU2(resp, vec2( sLlantaIzqA, 2.0));
    resp=opU2(resp, vec2( sLlantaDerA, 2.0));
    resp=opU2(resp, vec2( sLlantaIzqB, 2.0));
    resp=opU2(resp, vec2( sLlantaDerB, 2.0));


    return resp;

}




vec2 Vehiculov2(vec2 p )
{
    float sBase= sdBox(p-vec2(0.0,-0.5), vec2(0.1,0.2) );

    float sLlantaIzqA= sdBox(p-vec2(-0.12,-0.4), vec2(0.03,0.06) );
    float sLlantaDerA= sdBox(p- vec2(0.12,-0.4), vec2(0.03,0.06) );
    float sLlantaIzqB= sdBox(p-vec2(-0.12,-0.6), vec2(0.03,0.06) );
    float sLlantaDerB= sdBox(p-vec2( 0.12,-0.6), vec2(0.03,0.06) );

    vec2 resp=vec2(9999.9,-1.0);

    resp=opU2(resp, vec2( sBase, 8.0));
    resp=opU2(resp, vec2( sLlantaIzqA, 7.0));
    resp=opU2(resp, vec2( sLlantaDerA, 7.0));
    resp=opU2(resp, vec2( sLlantaIzqB, 4.0));
    resp=opU2(resp, vec2( sLlantaDerB, 4.0));


    return resp;

}

vec2 ArbolyPoste(vec2 p1, vec2 p2)
{

    vec2 c2=vec2(10.0,1.5);
    vec2 pr2=opRep2D(p1,c2);

    float PosteA= sdBox(pr2-vec2(-1.7,-0.3), vec2(0.02,0.2) );
    vec2 resp=vec2(9999.9,-1.0);
    resp=opU2(resp, vec2( PosteA, 14.0));
    return resp;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv0 = fragCoord/iResolution.xy;
    vec2 p0 = (2.*fragCoord-iResolution.xy)/iResolution.y;

     vec2 pos = (fragCoord.xy / iResolution.xy) - vec2(0.5);	
        float horizon = 0.0; 
        float fov = 0.25; 
	float scaling = 0.6541;
	float size = 0.21;

	vec3 p3 = vec3(pos.x, fov, pos.y - horizon);      
	vec2 s = vec2(p3.x / p3.z, p3.y / p3.z) * scaling;

    vec2 p=s;



	vec3 col = vec3(0.280,0.320,1.0);
    col = getColorf(12.0);


    float t1=iTime*2.0;
    vec2 p1=p;
    p1.y-=t1;

    vec2 p1auto=p;
    p1auto.x+=0.5*sin(t1+0.5*sin(t1*2.0-1.0));

    vec2 p2=p;


    vec2 s1=Pista(p1);
    col=DibujoConBordeVec2(p, s1,col);

    vec2 V1= Vehiculo(p1auto);
    col=DibujoConBordeVec2(p, V1,col);


    vec2 c=vec2(10.0,8.0);
    vec2 repAuto2= opRep2D(p, c );
    float trep=mod(t1,8.0);

    vec2 p2auto=p;
    p2auto=repAuto2+vec2(0.75*sin(0.25*trep),2.0-trep*0.5);
    vec2 V2= Vehiculov2(p2auto);
    col=DibujoConBordeVec2(p, V2,col);



    vec2 p3auto=p;
    p3auto=repAuto2-vec2(0.75*sin(0.35*trep),2.0);
    p3auto.y+=trep*0.5;

    vec2 V3= Vehiculov2(p3auto);
    col=DibujoConBordeVec2(p, V3,col);

    vec2 Ar1=ArbolyPoste(p0,p);
    col=DibujoConBordeVec2(p, Ar1,col);


     if ((p.y)>0.0)
        col = vec3(0.0,0.0,0.2+1.0*sin(iTime*0.5));

    fragColor = vec4(col,1.0);


}

