void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    fragColor = vec4(texture(iChannel0, fragCoord.xy/iResolution.xy).rrr, 1.0);
}

#define TIME iTime *0.5
#define R (iResolution.xy)
#define mR min(R.x, R.y)
#define T(U) texelFetch( iChannel0, ivec2(U), 0 )
const float RADIUS = 0.05; //decrease this
const float SPACING = 2.5; //increase this
const float THICKNESS = 0.05;

vec2 rot(vec2 p, float inc){ return vec2(sin((TIME+inc)*p.x),cos((TIME+inc)*p.y)); }

vec2 pMod(in vec2 inp, out vec2 outp, float size){
	outp = mod(inp+size*0.5,size)-size*0.5; return floor((inp+size*0.5)/size);
}
float line(vec2 p, vec4 ab) {
    p -= ab.xy, ab.zw -= ab.xy;
    return smoothstep(3./mR,0.,length(p-ab.zw*(clamp(dot(p,ab.zw)/dot(ab.zw,ab.zw),0.,1.))));
}

void mainImage( out vec4 C, in vec2 P ){
    if (P == vec2(0.5,0.5)){ C.a=R.x; C.g=RADIUS; C.b=THICKNESS; return; }
    vec4 pc = T(vec2(0,0));
    if (R.x!=pc.a || RADIUS!=pc.g || THICKNESS!=pc.b){ C = vec4(0); return; }

    vec2 uv = P/mR - vec2(RADIUS*1.25, 1.0-RADIUS*1.25);
    vec2 puv; vec2 id = pMod(uv, puv, RADIUS*SPACING);
    C.r = line(vec2(puv.x, uv.y), RADIUS*vec4(rot(id.xx,0.), rot(id.xx,THICKNESS)));
    C.r += line(vec2(uv.x, puv.y), RADIUS*vec4(rot(id.yy,0.), rot(id.yy,THICKNESS)));
    C.r += id.x>0.0 && id.y!=0.0 ? line(puv, RADIUS*vec4(rot(id,0.), rot(id,THICKNESS))) : 0.;
    C.r *= id.x==0.0 && id.y==0.0 ? 0.0 : 1.0;
    C.r = mix(0.997*texture(iChannel0, P/R).r, 1.0, C.r);
}
