float hash21(vec2 p) {
  p = fract(p * vec2(233., 851.));
  p += dot(p, p + 23.);
  return fract(p.x * p.y);
}

vec4 hexCoords(vec2 p) {
    vec2 normal = vec2(1, 1.7320508075688772);
    vec2 h = normal * .5;

    vec2 a = mod(p, normal) - h;
    vec2 b = mod(p - h, normal) - h;

    vec2 gv = dot(a, a) < dot(b, b) ? a : b;

    return vec4(gv, p - gv);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - .5 * iResolution.xy)/iResolution.y;
    uv += vec2(sin(.11 * iTime), cos(.13 * iTime)) * .2;
    uv *= 10. + 2. * cos(iTime * .1);

    vec4 hc = hexCoords(uv);

    bool nemo = length(hc.zw - vec2(4., -2.)) < .5;
    float r0 = nemo ? .2 : .12;
    float r1 = nemo ? .3 : .4;
    float w = .06;
    float om = 2.;

    float r = r0 + (sin(om * iTime + hc.z * .71) * sin(om * iTime + hc.w * 1.1) + 1.) * .53 * (r1 - r0);

    float c = length(hc.xy);

    c = smoothstep(-.01, .01, abs(c - r) - w);

    vec3 lightblue = vec3(67., 101., 153.) / 255.;
    vec3 darkblue = vec3(16., 17., 73.) / 255.;
    vec3 orange = vec3(234., 154., 58.) / 255.;

    vec3 col = c * darkblue;
    col += (1. - c) * (nemo ? orange : lightblue);

    fragColor = vec4(col,1.0);
}
