void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;


    float f = 0.;
    f+= smoothstep(.011,.01,abs(uv.y-uv.x));
    f+= smoothstep(.011,.01,abs(uv.y-(1.-uv.x)));
    f+= smoothstep(.011,.01,abs(uv.y-.5));
    f+= smoothstep(.00611,.0061,abs(uv.x-.5));
    // Output to screen
    fragColor = vec4(f);
}
