/*
//absolute coordinates
vec2 A = vec2(50., 50.);
vec2 B = vec2(200., 375.);
vec2 C = vec2(600., 50.);
*/

//relative coordinates
vec2 A = vec2(.125, .125);
vec2 B = vec2(.35, .85);
vec2 C = vec2(.85, .125);

vec4 triangleColor = vec4(0.75, 0.75, 0.75, 1.);


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    vec4 baseColor = vec4(col,1.0);

    /*
    //absolute triangle
    vec2 xy = fragCoord;
    if ((xy.x - A.x)/(B.x - A.x) < (xy.y - A.y)/(B.y - A.y) ||
        (xy.x - B.x)/(C.x - B.x) > (xy.y - B.y)/(C.y - B.y) ||
        (xy.x - C.x)/(A.x - C.x) > (xy.y - C.y)/(A.y - C.y))
    {fragColor = baseColor;}
    else {fragColor = triangleColor;};
    */

    //relative triangle
    if ((uv.x - A.x)/(B.x - A.x) < (uv.y - A.y)/(B.y - A.y) ||
        (uv.x - B.x)/(C.x - B.x) > (uv.y - B.y)/(C.y - B.y) ||
        (uv.x - C.x)/(A.x - C.x) > (uv.y - C.y)/(A.y - C.y))
    {fragColor = baseColor;}
    else {fragColor = triangleColor;};

}
