    f = texelFetch(iChannel0, ivec2(g),0);
}

//I hope this helps new users start programming and learning math easily ...
//Happy New Year 2020
void mainImage( out vec4 f, vec2 g )
{
    f.xyz = iResolution;
    vec2 v = (g+g-f.xy)/f.y, r;
    f *= texture(iChannel0, g/f.xy) / length(f);
    r.x = iTime;
    r.y = length(v) * 0.76 + sin(r.x*.3) * 1.;
    r.x += atan(v.x, v.y) * 76.,
    f += .0076/dot(
        r = fract(r)-.76,
        r);
}
