mat2 ro (float a) {
	float s = sin(a),c = cos(a);
    return mat2(c,-s,s,c);
}
vec4 trilinear(vec3 pos)
{
    float zi = floor(pos.z);
    float zf = pos.z - zi;
    return texture(iChannel0,d2(vec3(pos.xy, zi))/R)*(1.-zf) + texture(iChannel0,d2(vec3(pos.xy, zi+1.))/R)*zf;
}
void mainImage( out vec4 Q, in vec2 u )
{
    R = iResolution.xy;
    vec3 mi = 0.5*vec3(R/N,N*N);
    vec3 p = vec3(0,0,-.8*R.x/N);
    vec3 d = normalize(vec3((u-0.5*R)/R.y,1));
    if (iMouse.z>0.) {
 		p.xz *= ro(6.2*iMouse.x/R.x);
		d.xz *= ro(6.2*iMouse.x/R.x);
        p.yz *= ro(6.2*iMouse.y/R.y);
		d.yz *= ro(6.2*iMouse.y/R.y);
    } else {
		p.xz *= ro(.2*iTime);
		d.xz *= ro(.2*iTime);
	}
    Q = vec4(0);
    for (int i = 0; i < 120; i++) {
        vec3 o = abs(p)-mi;
        float m = length(max(o,0.));
        if (m<.1)
        { 	
            vec4 a = trilinear(p+mi);
            Q += .02*(1.-exp(-abs(length(a.xy)-1.)));
            p += d*exp(-6.*abs(length(a.xy)-1.));
        } else p += d*max(o.x,max(o.y,o.z));

 	} 

}

#define N (R.y>400.?10.:8.)
vec2 R;
#define R3D vec3(R/N,N*N)
vec2 d2 (vec3 U) {
    U = mod(U,R3D);
    U.z = floor(U.z);
	return U.xy+vec2(mod(U.z,N),floor(U.z/N))*R/N;
}
vec3 d3 (vec2 u) {
    vec2 o = floor(u/R*N);
	return vec3(mod(u,R/N),o.x+o.y*N);
}
mat2 e (float a) {
	float c = cos(a), s = sin(a);
    return mat2(c,-s,s,c);
}
#define A(U) texelFetch(iChannel0,ivec2(d2(U)),0)
#define B(U) texelFetch(iChannel1,ivec2(d2(U)),0)
//#define A(U) texture(iChannel0,d2(U)/R)
//#define B(U) texture(iChannel1,d2(U)/R)

#define F .5*(L.xy-.5*Q.xy*(dot(Q.xy,Q.xy)-length(Q.xy)))

void mainImage( out vec4 Q, in vec2 u )
{
    R = iResolution.xy;
    vec3 U = d3(u);
    Q = A(U);
    vec4 m = 1./6.*(
        A(U+vec3(1,0,0))+
        A(U+vec3(0,1,0))+
        A(U+vec3(0,0,1))+
        A(U-vec3(1,0,0))+
        A(U-vec3(0,1,0))+
        A(U-vec3(0,0,1))
    );
    vec4 L = m-Q;
    Q.x += (F).y;
    Q+=.001*L;
    if (iFrame < 1) Q = vec4(0,1,0,0);
    U -= 0.5*R3D;
    U.xz *= e(.1*iTime);
    U.zy *= e(.1*iTime);
    if (length(U.xy)<8.&&abs(U.z)<5.){
        Q = vec4(0,1,0,0);
        Q.xy *= e(2.*iTime+U.z);
    }
}

void mainImage( out vec4 Q, in vec2 u )
{
    R = iResolution.xy;
    vec3 U = d3(u);
    Q = A(U);
    vec4 m = 1./6.*(
        A(U+vec3(1,0,0))+
        A(U+vec3(0,1,0))+
        A(U+vec3(0,0,1))+
        A(U-vec3(1,0,0))+
        A(U-vec3(0,1,0))+
        A(U-vec3(0,0,1))
    );
    vec4 L = m-Q;
    Q.y -= (F).x;
}
