void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv = uv * 2.0 - 1.0;
	uv.y *= iResolution.y / iResolution.x;

    ////////// BLAHHHHH
    float dist = length(uv.xy);
    float sound = texelFetch(iChannel0, ivec2(64, 0),0).x;
    float sound2 = texelFetch(iChannel0, ivec2(4, 0),0).x;
    float time = iTime * 0.25;
    float ct = cos(time);
    float st = sin(time);
    uv *= mat2(ct, st, -st, ct);
    uv = vec2(length(uv) * sound * sound * 2.0, atan(uv.y, uv.x));
    float soundsample = texelFetch(iChannel0, ivec2(((uv.y / 3.14159265) * 0.5 + 0.5) * 12.,1),0).x * 2.0;
    float moduvx = mod(uv.x / pow(sound, 2.), 1.0);
    float pulse = smoothstep(moduvx - 0.02, moduvx + 0.02, .5 * soundsample) -  smoothstep(moduvx - 0.02, moduvx + 0.02, .35* soundsample);
    vec3 col = vec3(0.0);
    col.x = sin(uv.x * 2.0 + time * 5.0 + sound2 * 5.0) * cos(uv.y * 10.0 + time + uv.x * 15.0);
	col.y = sin(col.x * 10.0) * 0.5 + 0.5;
    col.x = 1.0-smoothstep(col.x - 0.05, col.x + 0.05, 0.5);
    col.z = sin(time * 0.5) * 0.5 + 0.5;
    float maskTerm = (uv.x - col.y * 0.5) * sound * sound * 10.5;
    float mask = smoothstep(maskTerm - 0.25, maskTerm + 0.25, col.z * 0.15);
    vec3 col2 = vec3(vec3(0.9, 0.4, 0.8)-pow(uv.x * 0.5, 1.5) * 3.5);
    col = mix(col, col2, mask);
    col *= clamp(pulse + 0.8,0.0, 1.0);
    fragColor = vec4(col * clamp(pow(dist, 0.4) + 0.0, 0.0, 1.0),1.0);
}
