// refs: https://en.wikipedia.org/wiki/YUV
//       https://en.wikipedia.org/wiki/YCbCr

void mainImage( out vec4 O, vec2 U )
{
    vec3 Y = vec3( .5, ( 2.*U/iResolution.xy - 1.) *.33 );
                                                          // gimp IUT-R470 ? 
  //O.rgb = mat3(1,1,1,  0,-.39,2.03,  1.14,-0.58,0) * Y  // BT.601 (flat ?)
  //O.rgb = mat3(1,1,1,  0,-.34,1.73,  1.37,-0.70,0) * Y; // ITU-R BT.601
  //                               + vec3(-.75,.46,-.93); // ? 
  //O.rgb = mat3(1,1,1,  0,-.21,2.13,  1.28,-0.38,0) * Y; // BT.709 (flat ?)
    O.rgb = mat3(1,1,1,  0,-.34,1.77,  1.40,-0.72,0) * Y; // JPEG

  //O = pow(O, vec4(1./2.2));                             // gamma conversion

    O = fract(O);                                         // to check overshoots
}
