bool yee (vec3 p)
{
 	if(p.y < 0.)
    {
        return true;
    }


    if(distance(p-1.5,vec3(0))<1.)
    {
        return true;
    }

    return false;
}

bool shadow (vec3 cp, vec3 ld)
{
    vec3 p = cp;
    vec3 d = ld;

    for(int i =0; i < 500; i++)
    {
        p += d/100.;
        if(yee(p))
        {
            return true;
        }
    }
    return false;
}

vec3 marchNotTheMonth(vec3 s, vec3 d)
{
    vec3 p = s;

    for(int i =0; i < 1000; i++)
    {
        p += d/200.;
        if(yee(p))
        {
            vec2 texUv = p.xz;
            bool s = shadow(p,vec3(1,1,0));
            if (s)
                return vec3(0.,0.,1.)/ 3.;
            return vec3(texture(iChannel0,texUv));
        }
    }

    return vec3(texture(iChannel1,d));
}
vec3 render(vec2 uv)
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 yo = uv*2.-1.;
    yo.x*=iResolution.x/iResolution.y;
    vec3 dir = vec3(yo,1);
    vec3 camera = vec3(1.5,1.5,0);
    vec3 march =  marchNotTheMonth(camera,dir);

    // Time varying pixel color
    vec3 col = vec3(march);



    return col;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 px = 1./ iResolution.xy;
	float _14 = 1./4.;
    float _34 = 3./4.;
    // Time varying pixel color
    vec3 col = vec3(0);


    col += render(uv + px*vec2(_14,_14));
    col += render(uv + px*vec2(_34,_14));
    col += render(uv + px*vec2(_14,_34));
    col += render(uv + px*vec2(_34,_34));
    col /= 4.;
	//if (uv.x>.5)
		//col = render(uv);
    //if (abs(uv.x-.5)<.01) col = vec3(0,1,0);
    // Output to screen
    fragColor = vec4(col,1.0);
}
